mapper.select:
SELECT rdate, productname, product_price, cnt, rank from(
             SELECT a.rdate, b.productname, b.product_price, a.cnt, rownum as rank 
             FROM pay_list a Inner Join products b ON a.productno = b.productno
             WHERE memberno=#{memberno}
             order by a.rdate ASC)
 where rank>=#{start_num} AND rank <= #{end_num}

SELECT count(*) 
      FROM pay_list a Inner Join products b ON a.productno = b.productno
      WHERE memberno=#{memberno}
